package main

import "testing"

func TestLongestPalindrome(t *testing.T) {
	got := longestPalindrome("eabcb")
	want := "bcb"

	if got != want {
		t.Errorf("got %s, want %s", got, want)
	}
}

func TestLongestPalindromer2(t *testing.T) {
	foo := ""
	got := longestPalindrome(foo)
	want := "ivi"

	if got != want {
		t.Errorf("got %s, want %s", got, want)
	}
}
